mapper.select:
SELECT *
FROM communities


        categories = #{category}


ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT * FROM communities
WHERE categories = #{category}
AND users = #{user}
ORDER BY id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT *
FROM communities
WHERE users IN

    #{userId}

AND categories = #{category}
ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE communities SET updated_at = NOW()
, `users` = #{users}
, `categories` = #{categories}
, `title` = #{title}
, `content` = #{content}
, `count` = #{count}
, `hash` = #{hash}
, `is_delete` = #{is_delete}
, `is_visible` = #{is_visible}
 WHERE `id` = #{id}

mapper.select:
SELECT * FROM communities WHERE 1=1
AND `id` = #{id}
AND `users` = #{users}
AND `categories` = #{categories}
AND `title` = #{title}
AND `content` = #{content}
AND `count` = #{count}
AND `hash` = #{hash}
AND `is_delete` = #{is_delete}
AND `is_visible` = #{is_visible}

        SELECT c.*, u.`account`, u.`name`, u.`img` FROM communities c
        INNER JOIN users u ON u.`id` = c.`users`


	            OR c.`id` = #{id}
		        OR c.`users` = #{users}
		        OR c.`categories` = #{categories}
		        OR c.`title` LIKE CONCAT('%',  #{title}, '%')
		        OR c.`content` LIKE CONCAT('%',  #{content}, '%')
		        OR c.`count` = #{count}
		        OR c.`hash` = #{hash}
