mapper.insert:
SELECT LAST_INSERT_ID()
INSERT INTO notifications ( created_at
, `id`
, `sender`
, `receiver`
, `board`
, `board_types`
, `content`
, `updated_at`
, `is_read`
, `is_delete`
) VALUES ( NOW()
, #{id}
, #{sender}
, #{receiver}
, #{board}
, #{board_types}
, #{content}
, #{updated_at}
, #{is_read}
, #{is_delete}
)

mapper.update:
UPDATE notifications SET updated_at = NOW()
, `sender` = #{sender}
, `receiver` = #{receiver}
, `board` = #{board}
, `board_types` = #{board_types}
, `content` = #{content}
, `is_read` = #{is_read}
, `is_delete` = #{is_delete}
 WHERE `id` = #{id}

mapper.select:
        SELECT n.*, u.`name`, u.`img` FROM notifications n
        INNER JOIN users u ON u.`id` = n.`sender`   


		        OR n.`id` = #{id}
		        OR n.`sender` = #{sender}
		        OR n.`receiver` = #{receiver}
		        OR n.`board` = #{board}
		        OR n.`board_types` = #{board_types}
		        OR n.`content` = #{content}
		        OR n.`is_read` = #{is_read}
		        OR n.`is_delete` = #{is_delete}

